def generate_seoul_grid():
    # 서울시의 대략적인 경계 (위도/경도)
    bounds = {
        'minLat': 37.413,
        'maxLat': 37.715,
        'minLon': 126.764,
        'maxLon': 127.269
    }
    
    # 각 구의 대략적인 중심 좌표 (위도, 경도)
    district_centers = {
        'Jongno-gu': [37.57, 126.979],
        'Jung-gu': [37.56, 126.997],
        'Yongsan-gu': [37.532, 126.99],
        'Seongdong-gu': [37.551, 127.037],
        'Gwangjin-gu': [37.538, 127.085],
        'Dongdaemun-gu': [37.574, 127.04],
        'Jungnang-gu': [37.606, 127.093],
        'Seongbuk-gu': [37.589, 127.017],
        'Gangbuk-gu': [37.64, 127.011],
        'Dobong-gu': [37.669, 127.047],
        'Nowon-gu': [37.655, 127.057],
        'Eunpyeong-gu': [37.603, 126.929],
        'Seodaemun-gu': [37.579, 126.936],
        'Mapo-gu': [37.566, 126.908],
        'Yangcheon-gu': [37.517, 126.866],
        'Gangseo-gu': [37.551, 126.83],
        'Guro-gu': [37.495, 126.887],
        'Geumcheon-gu': [37.457, 126.896],
        'Yeongdeungpo-gu': [37.526, 126.896],
        'Dongjak-gu': [37.512, 126.939],
        'Gwanak-gu': [37.478, 126.952],
        'Seocho-gu': [37.484, 127.033],
        'Gangnam-gu': [37.497, 127.028],
        'Songpa-gu': [37.514, 127.106],
        'Gangdong-gu': [37.53, 127.124]
    }
    
    # 격자의 각 셀에 대해 가장 가까운 구 찾기
    def find_nearest_district(lat, lon):
        min_distance = float('inf')
        nearest_index = -1
        
        districts = list(district_centers.keys())
        
        for i, district in enumerate(districts):
            d_lat, d_lon = district_centers[district]
            distance = np.sqrt((lat - d_lat)**2 + (lon - d_lon)**2)
            
            if distance < min_distance:
                min_distance = distance
                nearest_index = i
        
        return nearest_index
    
    # 100x100 격자 생성
    seoul_grid = np.zeros((100, 100), dtype=int)
    
    for i in range(100):
        for j in range(100):
            # 격자 좌표를 실제 위도/경도로 변환
            lat = bounds['minLat'] + (bounds['maxLat'] - bounds['minLat']) * (i + 0.5) / 100
            lon = bounds['minLon'] + (bounds['maxLon'] - bounds['minLon']) * (j + 0.5) / 100
            
            # 가장 가까운 구 찾기
            seoul_grid[i, j] = find_nearest_district(lat, lon)
    
    return seoul_grid

