seoul_grid_rotated_flipped = flip_grid_horizontal(seoul_grid_rotated)

# 구별 중심점 찾기 함수
def find_district_centers(grid, district_id):
    """특정 구의 영역에서 라벨을 표시할 중심점 찾기"""
    mask = (grid == district_id)
    y_coords, x_coords = np.where(mask)
    
    if len(y_coords) == 0:
        return None, None
    
    # 구역의 중심 계산 (무게중심)
    y_center = int(np.mean(y_coords))
    x_center = int(np.mean(x_coords))
    
    # 실제 구역 내에 있는 점인지 확인 (경계가 복잡할 수 있으므로)
    if not mask[y_center, x_center]:
        # 가장 가까운 유효한 점 찾기
        distances = np.sqrt((y_coords - y_center)**2 + (x_coords - x_center)**2)
        nearest_idx = np.argmin(distances)
        y_center = y_coords[nearest_idx]
        x_center = x_coords[nearest_idx]
    
    return y_center, x_center

# 구름에 배경 추가한 텍스트 표시 함수
def add_text_with_background(ax, x, y, text, fontsize=10, color='black', bg_color='white', alpha=0.7):
    """텍스트에 배경을 추가하여 가독성 향상"""
    return ax.text(x, y, text, fontsize=fontsize, color=color, 
                   ha='center', va='center', weight='bold',
                   bbox=dict(boxstyle="round,pad=0.3", facecolor=bg_color, alpha=alpha, edgecolor='none'))

