    # 현대적인 색상 팔레트
    colors = sns.color_palette("muted", 25)
    cmap = ListedColormap(colors)
    
    fig, ax = plt.subplots(figsize=(15, 15))
    
    # 최종 변환된 이미지 표시
    im = ax.imshow(seoul_grid_rotated_flipped, cmap=cmap, interpolation='nearest')
    
